function DiamondCell() {
  const [hovered, setHovered] = useState(false);

  const HOVER_COLORS = [
    "bg-blue-400/10! shadow-[0_0_35px_rgba(96,165,250,0.25)]!",
    "bg-cyan-400/10! shadow-[0_0_35px_rgba(34,211,238,0.25)]!",
    "bg-purple-400/10! shadow-[0_0_35px_rgba(192,132,252,0.25)]!",
    "bg-amber-400/10! shadow-[0_0_35px_rgba(251,191,36,0.25)]!",
  ];

  const hoverStyle = useMemo(
    () => HOVER_COLORS[Math.floor(Math.random() * HOVER_COLORS.length)],
    [],
  );

  return (
    <div
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      className="relative! aspect-square! w-full! h-full!"
    >
      {/* Subtle grid line */}
      <div className="absolute! inset-0! border! border-white/[0.03]!" />

      {/* Hover layer */}
      <div
        className={`absolute! border! border-blue-500/20! inset-0! transition-all! duration-300! ease-out! ${
          hovered ? hoverStyle : ""
        }`}
      />
    </div>
  );
}

const PricingCard = ({
  title,
  plan,
  description,
  monthly,
  yearly,
  features,
  billing,
  mostPopular = false,
}) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [coupons, setCoupons] = useState([]);
  const BACKEND_URL = import.meta.env.VITE_BACKEND_URL;
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    userEmail: "",
    details: "",
    budget: "₦500k - ₦1M",
    timeline: "2-4 Weeks",
  });

  useEffect(() => {
    const fetchCoupons = async () => {
      try {
        const res = await fetch(`${BACKEND_URL}/api/coupon/`);
        const data = await res.json();
        if (data.success) setCoupons(data.coupons);
      } catch (err) {
        // Silently fail — coupons are optional
      }
    };
    fetchCoupons();
  }, [BACKEND_URL]);

  const handleAction = (selectedPlan) => {
    if (selectedPlan === "Enterprise" || title === "Enterprise") {
      setIsModalOpen(true);
    } else {
      handlePay(selectedPlan);
    }
  };

  const handlePay = async (selectedPlan) => {
    try {
      const { user, token, store } = useAuthStore.getState();
      if (!token || !user) {
        window.location.href = "/auth/sign-up";
        return;
      }
      if (!store?._id) {
        toast.error("No active store selected");
        return;
      }

      // Note: As you mentioned, this transaction is to a subaccount
      const amount =
        selectedPlan === "Starter"
          ? 29000
          : selectedPlan === "Professional"
            ? 99000
            : 299000;

      const res = await fetch(`${BACKEND_URL}/api/paystack/init`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          email: user.email,
          plan: "PAID",
          amount,
          storeId: store._id,
        }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Payment failed");
      window.location.href = data.url;
    } catch (err) {
      toast.error(err.message);
    }
  };

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
      const response = await fetch(`${BACKEND_URL}/api/auth/contact-sales`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        toast.success("Message sent! We'll be in touch.");
        setIsModalOpen(false);
        setFormData({
          fullName: "",
          userEmail: "",
          details: "",
          budget: "₦500k - ₦1M",
          timeline: "2-4 Weeks",
        });
      } else {
        throw new Error("Failed to send inquiry");
      }
    } catch (err) {
      toast.error(err.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <div
        className={`bg-white mt-10! mb-10! flex flex-col gap-6 rounded-3xl border p-8 shadow-sm w-[300px]!  md:min-w-[500px]! lg:min-w-full! md:h-full! md:w-full! mx-auto relative transition-all duration-300 hover:shadow-xl ${
          mostPopular
            ? "border-[#4f46e5] ring-1 ring-[#4f46e5] scale-105 z-10"
            : "border-gray-100 dark:border-gray-800"
        } ${
          title === "Professional"
            ? "!bg-[#1c1c1e] text-white!" // Use ! at the start, and ensure text is white
            : "bg-white  text-gray-900 "
        } `}
      >
        {/* Most Popular Badge */}
        {mostPopular && (
          <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
            <span className="bg-[#4f46e5] text-white text-[10px] font-bold sm:px-4! px-2! py-1 rounded-full uppercase tracking-widest">
              Most Popular
            </span>
          </div>
        )}

        {/* Coupon Badge */}
        {coupons.length > 0 && (
          <span className={`absolute top-4 right-4 inline-flex items-center gap-1 font-bold bg-indigo-50 text-[#4f46e5] text-[10px] px-2 py-1 rounded-lg border border-indigo-100 animate-pulse ${title === "Professional" ? "block!" : "hidden!"}`}>
            🏷️{" "}
            {coupons[0].discountType === "percentage"
              ? `${coupons[0].discountPercent}% OFF`
              : `DISCOUNT`}
          </span>
        )}

        <div className="text-center sm:text-left">
          <div
            className={`${title === "Professional" ? "text-white!" : ""} text-2xl font-bold text-gray-900 `}
          >
            {title}
          </div>
          <div
            className={`text-sm  mt-2 min-h-[40px] ${title === "Professional" ? "text-white!" : "text-gray-500!"} `}
          >
            {description}
          </div>

          <div className="mt-6 flex items-baseline justify-center sm:justify-start gap-1">
            <span
              className={`${title === "Professional" ? "text-white!" : "text-gray-900!"}  text-4xl font-extrabold  `}
            >
              {billing === "monthly" ? monthly : yearly}
            </span>
            {title !== "Enterprise" && (
              <span
                className={` text-sm ${title === "Professional" ? "text-neutral-100!" : "text-gray-500!"} `}
              >
                /{billing === "monthly" ? "mo" : "yr"}
              </span>
            )}
          </div>
        </div>

        <div className="flex-1">
          <ul className="space-y-4">
            {features.map((feature, idx) => (
              <li
                key={idx}
                className={`flex items-start text-sm ${title === "Professional" ? "text-white!" : "text-gray-600!"}`}
              >
                <Check
                  className="h-5 w-5 text-[#4f46e5] mr-3 flex-shrink-0"
                  strokeWidth={3}
                />
                {feature}
              </li>
            ))}
          </ul>
        </div>

        <button
          onClick={() => handleAction(plan)}
          className={`w-full  py-3.5 rounded-[100px]! font-bold transition-all ${
            mostPopular
              ? "bg-[#4f46e5] text-white hover:bg-[#4338CA] shadow-lg shadow-indigo-200"
              : "bg-gray-900 text-white hover:bg-black "
          }`}
        >
          {title === "Enterprise" ? "Contact Sales" : "Get Started"}
        </button>
      </div>

      {/* ENTERPRISE MODAL */}
      {isModalOpen && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm">
          <div className="bg-white dark:bg-gray-100 rounded-3xl shadow-2xl w-full max-w-lg overflow-hidden border dark:border-gray-800">
            <div className="p-8">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-2xl font-bold text-gray-900 ">
                  Custom Request Website
                </h3>
                <button
                  onClick={() => setIsModalOpen(false)}
                  className="p-2 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-full transition-colors"
                >
                  <X size={20} />
                </button>
              </div>

              <form className="space-y-4" onSubmit={handleContactSubmit}>
                <div className="grid grid-cols-1! sm:grid-cols-2! gap-4!">
                  <input
                    type="text"
                    placeholder="Full Name"
                    className="w-full border dark:border-gray-700  rounded-xl p-3 text-sm outline-none focus:ring-2 focus:ring-[#4f46e5]"
                    value={formData.fullName}
                    onChange={(e) =>
                      setFormData({ ...formData, fullName: e.target.value })
                    }
                    required
                  />
                  <input
                    type="email"
                    placeholder="Work Email"
                    className="w-full border dark:border-gray-700  rounded-xl p-3 text-sm outline-none focus:ring-2 focus:ring-[#4f46e5]"
                    value={formData.userEmail}
                    onChange={(e) =>
                      setFormData({ ...formData, userEmail: e.target.value })
                    }
                    required
                  />
                </div>
                <textarea
                  placeholder="Tell us about your project..."
                  className="w-full border dark:border-gray-700  rounded-xl p-3 text-sm outline-none focus:ring-2 focus:ring-[#4f46e5]"
                  rows="4"
                  value={formData.details}
                  onChange={(e) =>
                    setFormData({ ...formData, details: e.target.value })
                  }
                  required
                />
                <div className="grid! grid-cols-2! gap-4!">
                  <select
                    className="w-full border dark:border-gray-700  rounded-xl p-3 text-sm outline-none"
                    value={formData.budget}
                    onChange={(e) =>
                      setFormData({ ...formData, budget: e.target.value })
                    }
                  >
                    <option>₦500k - ₦1M</option>
                    <option>₦1M - ₦5M</option>
                    <option>₦5M+</option>
                  </select>
                  <select
                    className="w-full border dark:border-gray-700  rounded-xl p-3 text-sm outline-none"
                    value={formData.timeline}
                    onChange={(e) =>
                      setFormData({ ...formData, timeline: e.target.value })
                    }
                  >
                    <option>2-4 Weeks</option>
                    <option>1-3 Months</option>
                    <option>3+ Months</option>
                  </select>
                </div>
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="md:w-[400px]! w-[250px]! mx-auto! rounded-[100px]! bg-[#4f46e5] text-white py-2! md:py-4  font-bold hover:bg-[#4338CA] transition-all disabled:opacity-50 flex justify-center items-center gap-2"
                >
                  {isSubmitting ? "Sending..." : "Send Inquiry"}
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </>
  );
};
